<template>
    <div>
        Home
      <img id="test" src="data:image/png;base64,">
    </div>
</template>

<script>
    import UPNG from '../utils/UPNG'


    function arraybuffer2base64(res) {
      var binary = "";
      var bytes = new Uint8Array(res);
      var len = bytes.byteLength;
      for (var i = 0; i < len; i++) {
        binary += String.fromCharCode(bytes[i]);
      }
      var base64 = `data:image/png;base64,${window.btoa(binary)}`
      return base64;
    }




    export default {
        name: "Home",
        mounted() {
          const img = document.getElementById("test");
          img.onload = function () {
            const canvas = document.createElement('canvas');
            // 根据图片大小设置canvas大小
            canvas.width = img.width;
            canvas.height = img.height;

            const context = canvas.getContext("2d");
            // canvas绘制图片
            context.drawImage(this, 0, 0, img.width, img.height);
            // 通过canvas获取imageData
            const imageData = context.getImageData(0, 0, img.width, img.height);

            canvas.toBlob((blob)=> {
              const fileReader = new FileReader();
              fileReader.readAsArrayBuffer(blob);
              fileReader.onload = function (e) {
                console.log(e.target.result)

                const a =  UPNG.encode([
                  e.target.result
                ], 300, 300, 0)

                console.log(arraybuffer2base64(a))
              }
            })

           //
          }


          console.log(UPNG)
        }
    }
</script>

